};

type Clause = {
text: string;
citations: Citation[];
};

type Sentence = {
clauses: Clause[];
};

type Paragraph = {
id: string; // e.g. "topic_overview_and_consensus"
title: string;
sentences: Sentence[];
};

type Subtask = {
id: string;
title: string;
paragraphs: Paragraph[];
};

